    async deleteArticle(id) {
      if (confirm("Are you sure?")) {
        const res = await fetch(`api/articles/${id}`, {
          method: "DELETE"
        });
        res.status === 200 ? this.articles = this.articles.filter(article => article.id !== id) : alert("Error Deleting Article");
      }
    },
